Ops.Patch.PmYS0bp.PickImageByAPIData_SingleLayer = function()
{
CABLES.Op.apply(this,arguments);
const op=this;
const attachments=op.attachments={};
const
    triggerIn = op.inTrigger("Trigger"),
    layerIndex = op.inValueInt("Layer Index"),
    apiData = op.inArray("API Data"),
    apiData2 = op.inArray("Second Layer API Data"),
    intervalList = op.inArray("Interval List"),
    imagesetRootUrl = op.inString("Imageset Root Url", ""),
    regionStr = op.inString("Region String", ""),

    indexArray_Iconic = op.inArray("Iconic Image Index List"),
    indexArray_Generic = op.inArray("Generic Image Index List"),
    indexIndicatorArray_Iconic = op.inArray("Iconic Image Indicator List"),
    indexIndicatorArray_Generic = op.inArray("Generic Image Indicator List"),

    iconicIndexIndicatorOut = op.outArray("Iconic Image Index Indicator Array"),
    genericIndexIndicatorOut = op.outArray("Generic Image Index Indicator Array"),

    imagePathOut = op.outArray("Image Path"),
    imageRelativePathOut = op.outArray("Image Relative Path"),
    imageTypeOut = op.outArray("Type"),
    imageCategoryOut = op.outArray("Category"),
    imageIndexOut = op.outArray("Index"),

    imagePathOut2 = op.outArray("Second Layer Image Path"),
    imageRelativePathOut2 = op.outArray("Second Layer Image Relative Path"),
    imageTypeOut2 = op.outArray("Second Layer Type"),
    imageCategoryOut2 = op.outArray("Second Layer Category"),
    imageIndexOut2 = op.outArray("Second Layer Index");


// op.setPortGroup("Input Range", [old_min, old_max]);
op.setPortGroup("First Layer", [imagePathOut, imageRelativePathOut, imageTypeOut, imageCategoryOut, imageIndexOut]);
op.setPortGroup("Second Layer", [imagePathOut2, imageRelativePathOut2, imageTypeOut2, imageCategoryOut2, imageIndexOut2]);



let absolutePathArray = Array(1);
let relativePathArray = Array(1);
let typeArray = Array(1);
let categoryArray = Array(1);
let indexArray = Array(1);

let absolutePathArray2 = Array(1);
let relativePathArray2 = Array(1);
let typeArray2 = Array(1);
let categoryArray2 = Array(1);
let indexArray2 = Array(1);


let iconicIndexIndicatorArray = Array(1);
let genericIndexIndicatorArray = Array(1);

let needUpdate = false;


apiData.onChange =
intervalList.onChange =
imagesetRootUrl.onChange = setNeedUpdate;

indexArray_Iconic.onChange =
indexArray_Generic.onChange =
indexIndicatorArray_Iconic.onChange =
indexIndicatorArray_Generic.onChange = setNeedUpdate;

function setNeedUpdate()
{
    needUpdate = true;
}


triggerIn.onTriggered = updateImageList;

function updateImageList()
{
    if(needUpdate == false)
    {
        return;
    }
    needUpdate = false;

    const inArray = apiData.get();
    if (!inArray || inArray.length === 0
    || !intervalList.get() || intervalList.get().length === 0
    || indexArray_Iconic.get() == null || indexArray_Generic.get() == null)
    {
        imagePathOut.set([]);
        imageRelativePathOut.set([]);
        imageTypeOut.set([]);
        imageCategoryOut.set([]);
        imageIndexOut.set([]);
        return;
    }

    if(intervalList.get().length != indexArray_Iconic.get().length - 1
    || intervalList.get().length != indexArray_Generic.get().length - 1)
    {
        console.error("Layer " + layerIndex.get() + ":" + "Interval amount doesn't match the amount of imageset categories");
        console.log(intervalList.get().length + "," + indexArray_Iconic.get().length + "," + indexArray_Generic.get().length);
        return;
    }


    //
    const interval_list = intervalList.get();
    const iconic_index_list = indexArray_Iconic.get();
    const generic_index_list = indexArray_Generic.get();

    let iconic_indicator_list = indexIndicatorArray_Iconic.get();
    let generic_indicator_list = indexIndicatorArray_Generic.get();
    if(iconic_indicator_list == null || Array.isArray(iconic_indicator_list) == false)
    {
        console.log("Layer " + layerIndex.get() + ":" + "rebuild iconic_indicator_list");
        iconic_indicator_list = Array(iconic_index_list.length);
        for(let i=0; i<iconic_indicator_list.length; i++)
        {
            iconic_indicator_list[i] = 0;
        }
    }
    if(generic_indicator_list == null || Array.isArray(generic_indicator_list) == false)
    {
        console.log("Layer " + layerIndex.get() + ":" + "rebuild generic_indicator_list");
        generic_indicator_list = Array(generic_index_list.length);
        for(let i=0; i<generic_indicator_list.length; i++)
        {
            generic_indicator_list[i] = 0;
        }
    }


    // First Layer
    absolutePathArray.length = inArray.length;
    relativePathArray.length = inArray.length;
    typeArray.length = inArray.length;
    categoryArray.length = inArray.length;
    indexArray.length = inArray.length;


    for (let i = 0; i < inArray.length; i++)
    {
        let v = inArray[i];

        [indexArray[i], typeArray[i], categoryArray[i], relativePathArray[i] ,absolutePathArray[i]] =
        chooseImage(v, interval_list, iconic_index_list, iconic_indicator_list, generic_index_list, generic_indicator_list);
    }

    imagePathOut.setRef(absolutePathArray);
    imageRelativePathOut.setRef(relativePathArray);
    imageTypeOut.setRef(typeArray);
    imageCategoryOut.setRef(categoryArray);
    imageIndexOut.setRef(indexArray);


    // Second Layer
    const inArray2 = apiData2.get();
    if (!inArray2 || inArray2.length === 0)
    {
        imagePathOut2.set([]);
        imageRelativePathOut2.set([]);
        imageTypeOut2.set([]);
        imageCategoryOut2.set([]);
        imageIndexOut2.set([]);
        return;
    }

    absolutePathArray2.length = inArray2.length;
    relativePathArray2.length = inArray2.length;
    typeArray2.length = inArray2.length;
    categoryArray2.length = inArray2.length;
    indexArray2.length = inArray2.length;

    for (let i = 0; i < inArray2.length; i++)
    {
        let v = inArray2[i];

        [indexArray2[i], typeArray2[i], categoryArray2[i], relativePathArray2[i] ,absolutePathArray2[i]] =
        chooseImage(v, interval_list, iconic_index_list, iconic_indicator_list, generic_index_list, generic_indicator_list);
    }
    imagePathOut2.setRef(absolutePathArray2);
    imageRelativePathOut2.setRef(relativePathArray2);
    imageTypeOut2.setRef(typeArray2);
    imageCategoryOut2.setRef(categoryArray2);
    imageIndexOut2.setRef(indexArray2);



    // iconicIndexIndicatorArray.length = indexArray_Iconic.get().length;
    // genericIndexIndicatorArray.length = indexArray_Generic.get().length;
    iconicIndexIndicatorOut.setRef(iconic_indicator_list);
    genericIndexIndicatorOut.setRef(generic_indicator_list);

    console.log("Layer " + layerIndex.get());
    console.log(iconic_indicator_list);
    console.log(generic_indicator_list);

}



function chooseImage(v, interval_list, iconic_index_list, iconic_indicator_list, generic_index_list, generic_indicator_list)
{
    let image_index = 0;
    let image_type = "";
    let category = "";
    let relative_path = "";
    let absolute_path = "";


    if(v == 0)
    {
        return [image_index, image_type, category, relative_path, absolute_path];
    }

    // find segment
    let segment_index = interval_list.length;
    for(let k=0; k<interval_list.length; k++)
    {
        if(v < interval_list[k])
        {
            segment_index = k;
            break;
        }
    }

    // // choose iconic first
    let image_indicator = iconic_indicator_list[segment_index];
    if(image_indicator < iconic_index_list[segment_index].length)
    {
        image_index = iconic_index_list[segment_index][image_indicator];
        image_type = regionStr.get();//"iconic";
        category = "h"+segment_index.toString();
        relative_path = image_type + "/" + category + "/" + (image_index).toString() + ".png";
        absolute_path = imagesetRootUrl.get() + "/" + relative_path;
        iconic_indicator_list[segment_index] += 1;
    }

    // choose generic if all iconic have been selected
    else
    {
        image_indicator = generic_indicator_list[segment_index];
        if(image_indicator >= generic_index_list[segment_index].length)
        {
            // Run out of image
            console.error("Run out of image in segment " + "h" + segment_index.toString());
            image_indicator = image_indicator % generic_index_list[segment_index].length;
            generic_indicator_list[segment_index] = image_indicator;
        }
        image_index = generic_index_list[segment_index][image_indicator];

        image_type = "generic";
        category = "h"+segment_index.toString();
        relative_path = image_type + "/" + category + "/" + (image_index).toString() + ".png";
        absolute_path = imagesetRootUrl.get() + "/" + relative_path;
        generic_indicator_list[segment_index] += 1;
    }

    return [image_index, image_type, category, relative_path, absolute_path];
}

apiData.set(null);
intervalList.set(null);


imagePathOut.set(null);
imageRelativePathOut.set(null);
imageTypeOut.set(null);
imageCategoryOut.set(null);
imageIndexOut.set(null);

imagePathOut2.set(null);
imageRelativePathOut2.set(null);
imageTypeOut2.set(null);
imageCategoryOut2.set(null);
imageIndexOut2.set(null);


updateImageList();


};

